mapper.select:
	select SECTORSNAME,SID from T_MEASURE_SECTOR_EXCEL 
	  
      where USER_ID=#{userId,jdbcType=DECIMAL} 
     GROUP BY SECTORSNAME,SID ORDER BY SID

	select MID,MS_EXCEL_DISPLAY,COLORNAME,COLORCODE from T_MEASURE_SECTOR_EXCEL 
	where  SECTORSNAME =#{sectorsName,jdbcType=OTHER}
	
      and where USER_ID=#{userId,jdbcType=DECIMAL}
