mapper.select:
   select 
   
   ,
   
   from T_EMISSION_DETAIL
   where EMission_Type=#{emtype,jdbcType=OTHER}  and Emission_Date BETWEEN #{startDate,jdbcType=OTHER}
      and #{endDate,jdbcType=OTHER}
      and code in 
      
    		 #{item,jdbcType=OTHER}
 		
		
         and SCENARINO_ID=#{scenarinoId,jdbcType=OTHER}
      
      order by Emission_Date

mapper.insert:
insert into T_EMISSION_DETAIL (EMISSION_ID, EMISSION_DATE, CODE, 
  EMISSION_DETAILS,CODELEVEL,MEASURE_REDUCE,EMISSION_TYPE, SCENARINO_ID)
values (#{emissionId,jdbcType=DECIMAL}, #{emissionDate,jdbcType=DATE}, #{code,jdbcType=VARCHAR}, 
  #{emissionDetails,jdbcType=CLOB}, #{codeLevel,jdbcType=VARCHAR},#{measureReduce,jdbcType=CLOB}, #{emissionType,jdbcType=VARCHAR},
  #{scenarunoId,jdbcType=DECIMAL}
  )

mapper.select:
select 

,

from T_EMISSION_DETAIL
where 1=1
 
   and EMISSION_DATE = #{emissionDate,jdbcType=DATE}
  
  
   and CODE = #{code,jdbcType=VARCHAR}
  
  
   and EMISSION_DETAILS = #{emissionDetails,jdbcType=CLOB}
  
  
   and CODELEVEL = #{codeLevel,jdbcType=VARCHAR}
  
  
   and EMISSION_ID = #{emissionId,jdbcType=DECIMAL}
   
   
   and MEASURE_REDUCE = #{measureReduce,jdbcType=CLOB}
  
  
   and EMISSION_TYPE = #{emissionType,jdbcType=VARCHAR}
  
  
   and SCENARINO_ID = #{scenarunoId,jdbcType=DECIMAL}

select
,
from T_EMISSION_DETAIL where CODE = #{code,jdbcType=VARCHAR} and EMISSION_DATE = #{emissionDate,jdbcType=DATE}
